        super(props);
        this.state = {
            isSmall: (window.innerWidth < 560),
        };
    }
    checkWidthResize = () => {
        const isSmall = window.innerWidth < 560;
        if ( isSmall !== this.state.width) {
            this.setState({ isSmall })
